import React ,{useEffect,useState} from 'react'
import './treelio.css'
import Trellio1 from '../images/trello1.webp'
import Trellio2 from '../images/img2.webp'
import Trellio3 from '../images/trello3.webp'
 const Treelio = () => {
//     const a2Elements = document.querySelectorAll('.eles');
// const cards=document.querySelectorAll('.carder');
// console.log(a2Elements);
// const imagy=document.getElementById("img1");
// cards.forEach((element) => {
//     element.addEventListener('click', function() 
//     {
//         const eles = element.querySelector(".eles");
//         console.log(eles.dataset.imagurl);
//         imagy.src=eles.dataset.imagurl;
//         imagy.classList.add('image-transition');
//         a2Elements.forEach((el) => {
//             el.classList.remove('a2');
//         });
//         eles.classList.add('a2');
//     });
    
// });
const [selectedImage, setSelectedImage] = useState(Trellio1);

    const handleCardClick = (imageURL) => {
        setSelectedImage(imageURL);
    };

    useEffect(() => {
        const cards = document.querySelectorAll('.carder');

        cards.forEach((element) => {
            element.addEventListener('click', function () {
                const eles = element.querySelector(".eles");
                handleCardClick(eles.dataset.imagurl);
            });
        });
    }, []);
  return (
    <div className="secondsec">
        <p className="t1">TRELLO 101</p>
        <h2 className="t2">A productivity powerhouse</h2>
        <div className="t3">Simple, flexible, and powerful. All it takes are boards, lists, and cards to get a clear view of
            who’s doing what
            and what needs to get done. Learn more in our guide for getting started.</div>
        <div className="secsec">
            <div className="cardst">
                <div className="carder cardercopy">
                    <div className="a2 eles" data-imagurl={Trellio1}>
                        <p className="etext">bf</p>
                    </div>
                    <div>
                    <h3>Boards</h3>
                    <p className="ct">Trello boards keep tasks organized and
                        work moving forward. In a glance, see
                        everything from "things to do" to "aww
                        yeah, we did it!"</p>
                    </div>
                </div>
                <div className="carder cardercopy">
                    <div className="eles" data-imagurl={Trellio2}>
                        <p className="etext">bf</p>
                    </div>
                    <div>
                    <h3>Lists</h3>
                    <p className="ct">The different stages of a task. Start as
                        simple as To Do, Doing or Done-or build
                        a workflow custom fit to your team's
                        needs. There's no wrong way to Trello.</p>
                    </div>
                </div>
                <div className="carder cardercopy">
                    <div className="eles" data-imagurl={Trellio3}>
                        <p className="etext">bf</p>
                    </div>
                    <div>
                    <h3>Cards</h3>
                    <p className="ct">Trello boards keep tasks organized and
                        work moving forward. In a glance, see
                        everything from "things to do" to "aww
                        yeah, we did it!"</p>
                    </div>
                </div>
            </div>
            <div className="imagesinthis">
                <img id="img1" className="image-transition" src={Trellio1} />
            </div>
        </div>
    </div>
  )
}
export default Treelio;
